insert into Books (Name, Author, Year, Publisher, Description, Genre, NumberOfPages, Count) 
values ('Fale','Virginia Woolf', 2020, 'Wydawnictwo Literackie', 'ale, wydane w 1931 roku, uchodzą za jedną z najbardziej eksperymentalnych powieści Virginii Woolf, która wykorzystuje tu technikę strumienia świadomości, miesza swobodnie poezję z prozą, koncentruje się na głębi psychologicznej bohaterów i - jak sama stwierdziła – przedkłada „rytm” nad „akcję”.', 'literatura piękna', 304, 3);

insert into Books (Name, Author, Year, Publisher, Description, Genre, NumberOfPages, Count) 
values ('Samotna','Lisa Gardner', 2020, 'Albatros', 'Bobby Dodge, snajper policji stanowej Massachusetts, obserwuje przez lunetę tragiczne wydarzenia w domu na przeciwko. Uzbrojony mężczyzna zabarykadował się tam z żoną i dzieckiem. Mierzy do nich z broni, jego palec powoli naciska na spust. Bobby ma tylko ułamek sekundy na decyzję, która może zaważyć na całym jego życiu.', 'kryminał', 384, 3);

insert into Books (Name, Author, Year, Publisher, Description, Genre, NumberOfPages, Count) 
values ('Zielona Mila','Stephen King', 2020, 'Albatros', 'Akcja powieści, zbliżonej klimatem do głośnej noweli tego samego autora, Skazani na Shawshank, toczy się w Ameryce lat 30. Jej bohaterowie to więźniowie oczekujący na wykonanie kary śmierci i pilnujący ich strażnicy.', 'fantastyka', 416, 3);

insert into Books (Name, Author, Year, Publisher, Description, Genre, NumberOfPages, Count) 
values ('Poszukiwania','Nora Roberts', 2020, 'Świat Książki', 'Fiona, niedoszła ofiara seryjnego mordercy, usiłuje powrócić do normalnego życia. Na pięknej wyspie nieopodal Seattle prowadzi szkołę dla psów i podejmuje akcje ratunkowe. Niezbędne jej spokój i samotność zakłóca Simon, szorstki i skryty twórca artystycznych mebli, mający kłopot ze swym psem.', 'literatura obyczajowa', 448, 3);

insert into Books (Name, Author, Year, Publisher, Description, Genre, NumberOfPages, Count) 
values ('Wybaczam ci','Remigiusz Mróz', 2020, 'Czwarta Strona', 'Ina Kobryn prowadzi spokojne, uporządkowane życie. Spełnia się w pracy, wraz z mężem stara się o dziecko i właściwie nie ma powodów do zmartwień. Wszystko zmienia się, gdy pewnej nocy siada przed laptopem i dostrzega, że mąż nie wylogował się ze swojego konta na Facebooku. Jej wzrok pada na krótką wiadomość o treści „wybaczam ci”, pochodzącą od nieznajomej dziewczyny.', 'kryminał', 432, 3);